import React from 'react'
import { useState, useEffect } from 'react'
import './Style/Login.css'
import axios from './axios'

function Login() {

    const [login, setLogin] = useState('');
    const [pass, setPass] = useState('');
    const [user, setUser] = useState([]);

    const getAllUsers = () => {


        axios.get(`/get/userName?userName=${login}`).then((res) => {
            setUser(res.data)

        })} 

        useEffect (() => {

            getAllUsers();

        }, [login])


    const setConnected = (e) => {

        e.preventDefault();

        console.log()


        if (user.userPass === pass) {

            axios.post(`/update/Connected?userName=`,{
                connected: true
            })

        } else {
            alert("wrong password mate !")
        }


    }


    return (

        <div className='login__body'>

            <div className='login__infos'>

                <div className='login__input'>

                    <h1>Login</h1>
                    <input 
                            value={login}
                            onChange={(e) => setLogin(e.target.value)}
                            type='text' 
                            placeholder='Enter your login here' />

                </div>

                <div className='password__input'>

                    <h1>Password</h1>
                    <input 
                            value={pass}
                            onChange={(e) => setPass(e.target.value)}
                            type='text' placeholder='Enter your password here' />

                </div>

                <div onClick={setConnected} className='login__enter'>

                    Login

                </div>

            </div>

        </div>
    )
}

export default Login
