import React, { useState } from 'react'
import './Style/HeaderClient.css'
import SearchIcon from '@material-ui/icons/Search';
import ShoppingCartIcon from '@material-ui/icons/AddShoppingCart';
import FavoriteBorderIcon from '@material-ui/icons/FavoriteBorder';
import { auth, provider } from './firebase'
import axios from './axios'


function HeaderClient() {

    const number = 0;
    const [name, setName] = useState('')
    const [users, setUsers ] = useState([])

    const header__itemStyle  = {

        position: 'relative',
        top: '-1.5rem',
        left: '0.01rem',
        borderRadius: '50%'

    };

    const getAllClients = () => {
        axios.get('get/allClients').then((res) => {
            setUsers(res.data)
        })
    }

    const signIn = () => {

        auth.signInWithPopup(provider)
        .catch((error) => alert(error.message));

        auth.onAuthStateChanged(authUser => {

            if(authUser) {

            setName(authUser.displayName)

            axios.post('/new/client', {

                clientName: authUser.displayName,
                clientMail: authUser.email,
                isConnected: true
            })

            }
        })
    }



    const signOut = () => {

        axios.post(`/update/clienIsConnected?clientName=${name}`, {
            isConnected: false
        })

        window.location.reload();
    }

    return (

        <div className='header__main'>

            <div className='header__left'>


                <SearchIcon />
                <input placeholder='a3mil recherche' type='text'/>

            </div>

            <div className='header__middle'>

                <div className='header__option'>


                        <h1>Welcome {name}</h1>


                </div>

                <div className='header__option'>


                        <h3>bye :( </h3>


                </div>

            </div>

            <div className='header__right'>

                <div className='header__right__option'>
                    <ShoppingCartIcon fontSize='large'/>

                    <span className='header__item' style={ number === 0 ? { display: 'none' }: header__itemStyle }>
                        {number}
                    </span>

                </div>

                <div className='header__right__option'>

                    <FavoriteBorderIcon fontSize='large'/>
                    <span className='header__item' style={ number === 0 ? { display: 'none' }: header__itemStyle }>
                        {number}
                    </span>
                </div>


            </div>

        </div>

    )
}

export default HeaderClient
