import React, { useState, useEffect } from 'react'
import './Style/Books.css'
import BookElem from './BookElem'
import axios from './axios'

function Books() {

    const [books, setBooks] = useState([]);

    const getBooks = () => {
            axios.get('/get/allBooks').then((res) => {
                setBooks(res.data[0].allBooks)
            })
    }

    useEffect (() => {

        getBooks();

    }, [])

    console.log(books)


    return (

        <div className='books__body'>

            { books.map(({ bookName, author, price, quantity}) => (

                <BookElem src='https://images-na.ssl-images-amazon.com/images/I/51RjFV4mHdL._SX347_BO1,204,203,200_.jpg'
                        title={bookName}
                        author={author}
                        price={price}
                        qt={quantity}
                     />
            )


            )}

           <BookElem src='https://images-na.ssl-images-amazon.com/images/I/412CwEG7k7L._SX305_BO1,204,203,200_.jpg'
                     title='المقدمة'
                     author='ابن خلدون'
                     price='150 DT'

           />
           <BookElem src='https://www.ciscopress.com/ShowCover.asp?isbn=0135792738'
                     title='CCNA'
                     author='Cisco'
                     price='200 DT'
           />

        </div>
    )
}

export default Books
