import React from 'react'
import { useState, useEffect } from 'react';

import './Style/Card.css'
import { Link, Route, useRouteMatch } from "react-router-dom";
import ShoppingCartIcon from '@material-ui/icons/AddShoppingCart';
import FavoriteBorderIcon from '@material-ui/icons/FavoriteBorder';
import axios from './axios'
import Description from './Description'

function Card({ id, title, imageUrl, body }) {

    //const bookPath = 
    const qt = 5;
    const { url, path } = useRouteMatch();

    const [books, setBooks] = useState([]);

    const getBooks = () => {

            axios.get('/get/allBooks').then((res) => {
                setBooks(res.data)

            })
    }

    useEffect (() => {

        getBooks();

    }, [])


    return (
        <div>
        <Link to={`${url}/show_more`} className='link'>

            <div className='card-container'>

                <div className="image-container">
                    <img src={imageUrl} alt='Book cover'/>
                </div>

                <div className="card-content">

                    <div className="card-title" >
                        <h3>{title}</h3>
                    </div>

                    <div className='card-icons'>

                        <div className='item-1'>
                            <ShoppingCartIcon/>
                        </div>

                        <div className='item-2'>
                            <FavoriteBorderIcon/>
                        </div>

                    </div>

                    <div className="card-body">
                        <p>{body}</p>
                    </div>

                </div>

                <div className='show-more'>
                    <h3>Click me to show more </h3>
                </div>

                <div style={ qt != 0 ? {display: 'none'} : {display: 'block'} }>
                    <h1 className='sold-out'> Sold OUT ! ðŸ˜”</h1>
                </div>

            </div>

        </Link>

        <Route path={`${path}`}>

        {books.map(({ _id, bookSum, url }) => (



                <Description 
                        src={url}
                        summary={bookSum}/> 


                )
                )}
      </Route>
      </div>
    )
}

export default Card;
