import React, { useState, useEffect } from 'react'
import './Style_Admin/Toast.css'

function Toast({ position, color, display, icon, title, description }) {

    const [displayVal, setDisplay] = useState('none');

    return (

        <div className={`notification-container ${position}`} style={{display: display? display : displayVal}} 
        onClick={() => { setDisplay("none")
        }}>


            <div 

                className={`notification toast ${position}`}
                style={{ backgroundColor: color }}
                >
                <div className='delete__toast'>
                    X
                </div>
                            <div className="notification-image">
                                <img src="../icon.png" alt="" />
                            </div>
                            <div>
                                <p className="notification-title">{title}</p>
                                <p className="notification-message">
                                    {description}
                                </p>
                            </div>
                        </div>


        </div>
    )
}

export default Toast
