import React, { useState } from 'react'
import './Style/Body.css'
import axios from './axios'

function Body() {

    const [name, setName] = useState('');
    const [author, setAuthor] = useState('');
    const [genre, setGenre] = useState('');
    const [number, setNumber] = useState(0);
    const [summary, setSummary] = useState('');
    const [price, setPrice] = useState(0);
    const [qt, setQt] = useState(0);

    const addBook = (e) => {
        e.preventDefault();
        axios.post('/new/book', {

            bookName: name,
            author: author,
            genre: genre,
            numPages: number,
            bookSum: summary,
            price: price,
            quantity: qt,
            timestamp: Date.now()
        })

        setName("");
        setAuthor("");
        setGenre("");
        setNumber("");
        setSummary("");
        setPrice("");
        setQt("");
    };

    return (
        <div className='body__main'>

            <div className='body__info'>

                <div className='body__info__btn'>Simply Scan a Tag</div>

                <div className='body__info__add'>

                    <h3>Book name</h3>
                    <input 
                            value={name}
                            onChange={(e) => setName(e.target.value)}
                            type='text'
                            placeholder='Write book name here'/>

                </div>

                <div className='body__info__add'>

                    <h3>Book Author</h3>
                    <input placeholder='Write book author here'/>

                </div>

                <div className='body__info__add'>

                    <h3>Genre</h3>
                    <input placeholder='Write book genre here'/>

                </div>

                <div className='body__info__add'>

                    <h3>Number of pages</h3>
                    <input placeholder='Write number of pages here' type='number' min='0'/>

                </div>

                <div className='body__info__add'>

                    <h3>Book Summary</h3>
                    <textarea placeholder='Write book summary here' rows='5' cols='25'/>

                </div>

                <div className='body__info__add'>

                    <h3>Book Price</h3>
                    <input placeholder='Write book price here' type='number' min='0'/>

                </div>

                <div className='body__info__add'>

                    <h3>Quantity</h3>
                    <input placeholder='Write book quantity herer' type='number' min='0'/>

                </div>

                <button/>
                <div className='body__info__btn'>Add Book</div>

            </div>
            <img className='image__logo' src='icon.png' alt='E-Library Logo' width='350'/>
        </div>
    )
}

export default Body
