import React, { useState } from 'react'
import './Style_Admin/Body.css'
import axios from '../axios'

function Body() {

    const [name, setName] = useState('');
    const [author, setAuthor] = useState('');
    const [genre, setGenre] = useState('');
    const [number, setNumber] = useState('');
    const [summary, setSummary] = useState('');
    const [price, setPrice] = useState('');
    const [qt, setQt] = useState('');
    const [url, setUrl] = useState('');

    const addBook = (e) => {
        e.preventDefault();
        if ((name === '') || (author === '') || 
                            (genre === '') || (number === '') || 
                            (summary === '') || (price === '') || 
                            (qt === '') || (url === '')) {

                                alert('Still one missing information');

                            } else {

                                axios.post('/new/book', {

                                    bookName: name,
                                    author: author,
                                    genre: genre,
                                    numPages: number,
                                    bookSum: summary,
                                    price: price,
                                    quantity: qt,
                                    url: url,
                                    timestamp: Date.now()
                                })

                                setName('');
                                setAuthor('');
                                setGenre('');
                                setNumber('');
                                setSummary('');
                                setPrice('');
                                setQt('');
                                setUrl('');

                            }


    };

    return (
        <div className='body__main'>

            <div className='body__info'>

                <div className='body__info__btn'>Simply Scan a Tag</div>

                <div className='body__info__add'>

                    <h3>Book name</h3>

                    <input 
                            value={name}
                            onChange={(e) => setName(e.target.value)}
                            type='text'
                            placeholder='Write book name here'/>

                </div>

                <div className='body__info__add'>

                    <h3>Book Author</h3>

                    <input 
                            value={author}
                            onChange={(e) => setAuthor(e.target.value)}
                            type='text'
                            placeholder='Write book author here'/>

                </div>

                <div className='body__info__add'>

                    <h3>Genre</h3>

                    <select 
                            value={genre}
                            onChange={(e) => setGenre(e.target.value)}
                            type='text'
                            placeholder='Write book genre here'>

                        <option value="Action and Adventure">Action and Adventure</option>
                        <option value="Classics">Classics</option>
                        <option value="Comic Book">Comic Book</option>
                        <option value="Detective and Mystery">Detective and Mystery</option>
                        <option value="Fantasy">Fantasy</option>
                        <option value="Historical Fiction">Historical Fiction</option>
                        <option value="Horror">Horror</option>
                        <option value="Literary Fiction">Literary Fiction</option>
                        <option value="Romance">Romance</option>
                        <option value="Science Fiction (Sci-Fi)">Science Fiction (Sci-Fi)</option>
                        <option value="Short Storie">Short Storie</option>
                        <option value="Literary Fiction">Literary Fiction</option>


                    </select>

                </div>

                <div className='body__info__add'>

                    <h3>Number of pages</h3>

                    <input 
                            value={number}
                            onChange={(e) => setNumber(e.target.value)}
                            type='number' 
                            min='0'
                            placeholder='Write number of pages here' />

                </div>

                <div className='body__info__add'>

                    <h3>Book Summary</h3>

                    <textarea 
                                value={summary}
                                onChange={(e) => setSummary(e.target.value)}
                                type='text'
                                rows='5' 
                                cols='25'
                                placeholder='Write book summary here' />

                </div>

                <div className='body__info__add'>

                    <h3>Book Price</h3>

                    <input 
                            value={price}
                            onChange={(e) => setPrice(e.target.value)}
                            type='number' 
                            min='0'
                            placeholder='Write book price here' />

                </div>

                <div className='body__info__add'>

                    <h3>Quantity</h3>

                    <input 
                            value={qt}
                            onChange={(e) => setQt(e.target.value)}
                            type='number' 
                            min='0'
                            placeholder='Write book quantity herer' />

                </div>

                <div className='body__info__add'>

                    <h3>Book Cover URL</h3>

                    <input 
                            value={url}
                            onChange={(e) => setUrl(e.target.value)}
                            type='text' 
                            placeholder='Past Book URL here' />

                </div>

                <div className='body__info__btn' onClick={addBook}>Add Book</div>

            </div>
            <img className='image__logo' src='/icon.png' alt='E-Library Logo' width='350'/>
        </div>
    )
}

export default Body
