import React from 'react'
import './Style/Catalogue.css'
import Card from './Card'

function Catalogue() {

    const [books, setBooks] = useState([]);

    const getBooks = () => {

            axios.get('/get/allBooks').then((res) => {
                setBooks(res.data)

            })
    }

    useEffect (() => {

        getBooks();

    }, [])

    return (

        <div className='catalogue__main'>

            <div className='catlaogue__card' >

                <Card
                    title='bookname'
                    imageUrl='https://pngimg.com/uploads/book/book_PNG2105.png'
                    body='description and summary fklqjlqflqjlqsfj'/>

            </div>

            { books.map(({ _id, bookName, author, price, quantity, url}) => (

                <Card id={_id} 
                        src={url}
                        title={bookName}
                        author={author}
                        price={price + ' DT'}
                        qt={quantity}/> 

                )

            )}


        </div>
    )
}

export default Catalogue
